export STOP_CONTAINER=true
export KILL_RUNNING_CHEF_UPDATE=false

export git_repo_url="git@github.com:TOTVS/mdmdevops.git"
export CODE_SH='/root/git_update.sh'
export code_dir='/root/chef'
export START_COMMAND="ssh root@172.17.0.1 docker start kitchen-cluster-node1 kitchen-cluster-node2 kitchen-cluster-node3"
export STOP_COMMAND="ssh root@172.17.0.1 docker stop kitchen-cluster-node1 kitchen-cluster-node2 kitchen-cluster-node3"

export POST_START_COMMAND="sleep 5;
ssh -p 5122 root@172.17.0.1 'cp /opt/hosts /etc/hosts; true';
ssh -p 5123 root@172.17.0.1 'cp /opt/hosts /etc/hosts; true';
ssh -p 5124 root@172.17.0.1 'cp /opt/hosts /etc/hosts; true';

ssh -p 5122 root@172.17.0.1 '/root/manage_all_services.sh start couchbase-server,elasticsearch';
sleep 5;
ssh -p 5123 root@172.17.0.1 '/root/manage_all_services.sh start couchbase-server,elasticsearch';
sleep 5;
ssh -p 5124 root@172.17.0.1 '/root/manage_all_services.sh start couchbase-server,elasticsearch';

sleep 10;

ssh -p 5122 root@172.17.0.1 '/root/manage_all_services.sh start mdm || true';
sleep 15;
ssh -p 5123 root@172.17.0.1 '/root/manage_all_services.sh start mdm || true';
sleep 15;
ssh -p 5124 root@172.17.0.1 '/root/manage_all_services.sh start mdm || true';

ssh -p 5122 root@172.17.0.1 '/root/manage_all_services.sh status couchbase-server,elasticsearch,mdm';
ssh -p 5123 root@172.17.0.1 '/root/manage_all_services.sh status couchbase-server,elasticsearch,mdm';
ssh -p 5124 root@172.17.0.1 '/root/manage_all_services.sh status couchbase-server,elasticsearch,mdm';
"

export PRE_STOP_COMMAND="
ssh -p 5122 root@172.17.0.1 'cat /root/chef_update/report.txt; /root/manage_all_services.sh stop mdm,elasticsearch,couchbase-server';
ssh -p 5123 root@172.17.0.1 'cat /root/chef_update/report.txt; /root/manage_all_services.sh stop mdm,elasticsearch,couchbase-server';
